import React, {Component} from "react";
// import {VolumeSlider} from '../components/slider'
import data from '../data.1.json'
import './slider.css'
import InputRange from 'react-input-range'
import 'react-input-range/lib/css/index.css'
import Button from '../components/button/button'
export class SliderPage extends Component{
    constructor(props) {
        super(props);
        this.handleFirstClick = this.handleFirstClick.bind(this);
        this.handleFSecondClick = this.handleFSecondClick.bind(this);
        this.state={
            value: 0,
            value4: {
                min: 5,
                max: 10,
              },
            isDubleSlider: false,
        }
    }

    localData = JSON.parse(JSON.stringify(data));
    section = this.localData.assessment.sections[0].subsections[1].groups[0].questions[1].answers;

    handleFirstClick(){
      this.setState({isDubleSlider:true})
    }

    handleFSecondClick(){
    this.setState({isDubleSlider:false})
    }



  render() {
    const isDubleSlider = this.state.isDubleSlider;
    let button;
        if(isDubleSlider) {
            button = <Button value={"double slider"} onClick={this.handleFSecondClick} ></Button>
        } else {
            button = <Button value={"slider"} onClick={this.handleFirstClick} ></Button>
        }
        return (
        <div className='componentSlider'>
            <SelectSlider isLoggedIn={isDubleSlider} value4={this.state.value4}/>
            {button}
            <div className='componentSlider-message'> You select {this.state.value}</div>
        </div>

    )
  }
}

export default class SelectSlider extends React.Component{
    constructor(props) {
        super(props);
        this.state={
            value: 0,
            value4: {
                min: 5,
                max: 10,
              },

        }
    }
    // if (isDubleSlider){
        render(){
      return ( 
          <InputRange
          maxValue={20}
          minValue={0}
          formatLabel={value => `${value} kg`}
          value={this.state.value4}
          onChange={value => this.setState({ value4: value })}
          onChangeComplete={value => console.log(value)} />
      )
        }
