import React, { Component } from "react";
import ReactDOM from "react-dom";
class ComponentLifecycle extends Component {
  constructor(props) {
    super(props);
    this.state = { val: 0 };
  }
  update = () => {
    this.setState({
      val: this.state.val + 1
    });
  };

  componentDidMount() {
    console.log("componentDidMount");
    this.setState({
      m: 1
    });
    this.inc = setInterval(this.update, 1000);
  }
  //   button = () => (
  //     <button onClick={this.update}>{this.state.val * this.state.m}</button>
  //   );

  mount = (update, val) => {
    ReactDOM.render(
      <Button update={this.update} val={this.state.val} />,
      document.getElementById("id")
    );
  };
  unmount() {}
  render() {
    return (
      <div className="lesson">
        <button onClick={this.mount(this.update, this.state.val)}>Mount</button>
        <button onClick={this.update}>Unmount</button>
        <div id="id" />
      </div>
    );
  }
}

export function Button(update, val) {
  return <button onClick={update}>{val}</button>;
}

export default ComponentLifecycle;
