import React from 'react'

export class TestRefs extends React.Component{
    constructor(){
        super();
        this.state={
             a: ''}
    }

    update=(e)=>{
        this.setState({
            a: e.target.value
        })
    }
    render(){
        return(
            <div className="lesson">
                 <h3> Lesson refs:</h3>
                 <input 
                   refs="a"
                   type="text"
                   onChange={this.update}/>
                   <p>{this.state.a}</p>
                   <hr/>


             </div>
        )
    }

}

class Input extends React.Component{
    render(){
        return(
            <input 
            refs="a"
            type="text"
            onChange={this.props.update}/>
        )
    }
}
