export class Clock extends React.Component{
    constructor(props){
        super(props)
        this.state = {
            date: new Date().toLocaleString()
        }
    }
    componentDidMount(){
        this.timer = setInterval(
            () => this.tick(),
            1000
        );
    }
    componentWillUnmount(){

    }

    tick(){
        this.setState=new Date
    }

    render(){
        return(
            <div className='clock-component'>
                <h4>{this.state.date}</h4>
            </div>
        );
    }
}
