import React,{Component} from 'react'

class Lesson3 extends Component {
    constructor() {
        super({
            className: 'container',
            children: 'Hello!!!',
        })
    }

    element =  (
        <div {...this.props} className='override-class-name'/>
    )

    render() {

        // return <div {...this.props} />
        
         return(
            <div className="lesson 3">
                 <h3> Lesson 3:</h3>
                 {this.element}
                 {/* <div {...this.props} /> */}
            </div>
         )
    }
}

export default Lesson3
