import React from "react";
import data from "../data/price.json";

class ProductCategoryRow extends React.Component {
    render() {
      return (
        <tr>
          <td colSpan="2">{this.props.category}</td>
        </tr>
      );
    }
  }
class ProductRow extends React.Component {
  render() {
    return (
      <tr>
        <td style={this.props.style}>{this.props.name}</td>
        <td>{this.props.price}</td>
      </tr>
    );
  }
}

export default class ProductTable extends React.Component {
  localData = JSON.parse(JSON.stringify(data));

  render() {
    debugger
    let lastCategory = null;
    const row = [];
    this.localData.forEach((i) => {

        if(i.category !== lastCategory){
           row.push (
           <ProductCategoryRow category={i.category} key={i.category}/>)
           lastCategory = i.category;
        }
        else{
            (
                 this.localData.map(element => (
                    <ProductRow
                      key={element.name}
                      name={element.name}
                      price={element.price}
                      style={element.stocked ? {  } : { color: "red" }}
                    />
                  ))
            )
        }

    });

    return (
      <div>
        <table>
          <thead>
            <tr>
              <td>Name</td>
              <td>Price</td>
            </tr>
          </thead>
          <tbody>{row}</tbody>
        </table>
      </div>
    );
  }
}
