import React, { Component } from 'react';

class StateLesson extends Component {
    constructor(props){
        super(props)
        this.state = {
            text: "standart text",
            num: 0,
        }
    }

    render() {
        return (
            <div className="lesson">
            <h3>Lesson State</h3>
                    <div>{this.state.text}</div>
                    <div>{this.state.num}</div>
            </div>
        );
    }
} 

export default StateLesson;
