import React from "react";
import data from "../data/price.json";

class ProductRow extends React.Component {
  render() {
    return (
      <tr style={this.props.stocked}>
        <td>{this.props.name}</td>
        <td>{this.props.price}</td>
      </tr>
    );
  }
}

export default class ProductTable extends React.Component {
  localData = JSON.parse(JSON.stringify(data));
  debugger
  render() {
    const row = this.localData.map(element => (
      <ProductRow
        name={element.name}
        price={element.price}
        key={element.name}
        // style={element.stocked ? { color: "red" } : { color: "black" }}
        style={{ color:'red' }}
      />
    ));
    return (
      <div>
        <table>
          <thead>

            <tr>
              <td>Name</td>
              <td>Price</td>
            </tr>
          </thead>
          <tbody>{row}</tbody>
        </table>
      </div>
    );
  }
}
